// ./models/user.js
var mongoose = require('./db');
var Schema = mongoose.Schema;
var UserSchema = new Schema({
  username: String,
  password: String,
  name: String,
  age: Number,
  sex: {
    type: String,
    enum: ['male','female']
  },
