// 多集合查询
ArticleModel.aggregate([
  {
    $lookup: {  // 连接操作符
      from: 'articlecate',  // 关联的集合
      localField: 'cid',   // 本集合关联的字段
      foreignField: '_id',  // 其他集合关联的字段
